INSERT INTO `book` (`bid`, `Path`, `Name`, `Author`, `Time`, `Publish`, `Isbn`, `Library`, `Introdu`, `Catalog`, `boInte`, `bofrie`, `collect`, `boScore`, `boPeople`, `popularity`) VALUES
(1, 'bookPic/sanguo.jpg', '三国演义', '罗贯中', '2010', '岳麓书社', '9787805200132', '<li>武汉市图书馆</li><li>在馆</li><li>国家图书馆</li><li>在馆</li><li>江苏省图书馆</li><li>在馆</li>', '<p>《三国演义》是根据三国时期的史实和民间传说创作而成的优秀历史小说。它的内容丰富多彩，为读者留下了深厚多而的认识价值。故事远起汉灵帝年间刘、关、张桃园结义，描述了东汉末年和三国时期近百年发生的重大历史事件，和众多的叱咤风云的英雄人物。作者通过真实动人的故事，揭示了封建统治阶级内部的黑暗和腐朽，控诉了统治者的暴虐和丑恶。&nbsp;&nbsp;<br></p>', '<p>第一回 宴桃园豪杰三结义 斩黄巾英雄首立功</p><p>第二回 张翼德怒鞭督邮 何国舅谋诛宦竖</p><p>第三回 议温明董卓叱丁原 馈金珠李肃说吕布&nbsp;</p><p>第四回 废汉帝陈留践位 谋董贼孟德献刀&nbsp;</p><p>第五回 发矫诏诸镇应曹公 破关兵三英战吕布</p>', '历史', NULL, 2, '8.3', 52, '0.5571541164260652'),
(2, 'bookPic/shuihu.jpg', '水浒传', '施耐庵', '2005', '人民文学出版社', '9787020008742', '<li>国家图书馆</li><li>在馆</li><li>苏州市图书馆</li><li>在馆</li><li>沈阳市图书馆</li><li>借出</li>', '<p>《水浒传》是我国第一部以农民起义为题材的长篇章回小说，是我国文学史上一座巍然屹立的丰碑，也是世界文学宝库中一颗光彩夺目的明珠。数百年来，它一直深受我国人民的喜爱，并被译为多种文字，成为我国流传最为广泛的古代长篇小说之一。&nbsp;&nbsp;<br></p>', '<p>第一回 张天师祈禳瘟疫 洪太尉误走妖魔<br>第二回 王教头私走延安府 九纹龙大闹史家村<br>第三回 史大郎夜走华阴县 鲁提辖拳打镇关西<br>第四回 赵员外重修文殊院 鲁智深大闹五台山<br>第五回 小霸王醉入销金帐 花和尚大闹桃花村&nbsp;&nbsp;<br></p>', '文学', NULL, 1, '4', 2, '0.5571541164260652'),
(3, 'bookPic/honglou.jpg', '红楼梦', '曹雪芹', '1996', '人民文学出版社', '9787020002207', '<li>武汉市图书馆</li><li>在馆</li><li>广东省图书馆</li><li>在馆</li>', '<p>《红楼梦》是一部百科全书式的长篇小说。以宝黛爱情悲剧为主线，以四大家族的荣辱兴衰为背景，描绘出18世纪中国封建社会的方方面面，以及封建专制下新兴资本主义民主思想的萌动。结构宏大、情节委婉、细节精致，人物形象栩栩如生，声口毕现，堪称中国古代小说中的经 典。&nbsp;&nbsp;<br></p>', '<p>第一回 甄士隐梦幻识通灵 贾雨村风尘怀闺秀<br>第二回 贾夫人仙逝扬州城 冷子兴演说荣国府<br>第三回 托内兄如海酬训教 接外孙贾母惜孤女<br>第四回 薄命女偏逢薄命郎 葫芦僧乱判葫芦案<br>第五回 游幻境指迷十二钗 饮仙醪曲演红楼梦<br>第六回 贾宝玉初试云雨情 刘姥姥一进荣国府&nbsp;&nbsp;<br></p>', '文学', NULL, 1, '5', 2, '0.43867935063970137'),
(4, 'bookPic/xiyou.jpg', '西游记', '吴承恩', '1994', '岳麓书社', '9787805200552', '<li>辽宁省图书馆</li><li>在馆</li>', '<p>《西游记》的艺术虚构正是建立在传统艺术经验和这种社会的宗教性观念和风习的基础之上的，但它又以作者融会了传统艺术经验所形成的艺术的独创性批判了社会的宗教性观念，或更正确地说，和社会的宗教性观念开了玩笑，进行了潮弄。这是这部演述超人间故事的神魔小说最突出、最优异的品质，也是它的艺术价值和魅力的最根本的所在。&nbsp;&nbsp;<br></p>', '<p>第一回 灵根育孕源流出 心性修持大道生<br>第二回 悟彻菩提真妙理 断魔归本合元神<br>第三回 四海千山皆拱伏 九幽十类尽除名<br>第四回 官封弼马心何足 名注齐天意未宁&nbsp;&nbsp;<br></p>', '文学', '赵六六', 1, '0', 1, '0.20654329147389294'),
(5, 'bookPic/liaozhai.jpg', '聊斋志异', '蒲松龄', '2002', '岳麓书社', '9787806651452', '<li>甘肃省图书馆</li><li>在馆</li><li>国家图书馆</li><li>在馆</li>', '<p>《聊斋志异》是我国清初的一部文言短篇小说集，共收小说近500篇，或讲民间的民俗民习、奇谈异闻、或讲世间万物的奇异变幻、题材极为广泛。其独特的故事情节、异彩纷呈的艺术形象，作者蒲松龄一生科甲不利、生活清贫，对于当时的社会有着深刻的认识。&nbsp;&nbsp;<br></p>', '<p>1 考城隍<br>2 耳中人<br>卷二<br>1 金世成<br>2 董生&nbsp;&nbsp;<br></p>', '文学', '赵六六', 2, '0', 1, '0.20654329147389294'),
(6, 'bookPic/rulin.jpg', '儒林外史', '吴敬梓', '2002', '人民文学出版社', '9787020036189', '<li>荆州市图书馆</li><li>在馆</li><li>吉林省图书馆</li><li>在馆</li>', '<p>吴敬梓的《儒林外史》是中国小说史上少有的几部出类拔萃的巨制之一，是唯一一部真正意义上的讽刺小说。</p><p>吴敬梓在创作此书时，以对待功名富贵和文行出处的态度为中心，贬抑批判士人中的假儒士和假名士，褒扬推崇真儒士和真名士，热烈赞美具有完整健康人格的、德才兼备的知识分子。</p><p>《儒林外史》创造了富有民族特色又极具个性的讽刺艺术。塑造人物时，能秉持公心，不虚美，不隐恶，借喜剧性的情节，揭示悲剧性的内涵，令人笑中带泪，具有振聋发聩的艺术效果。</p>', '<p>第一回 说楔子敷陈大义 借名流隐括全文<br>第二回 王孝廉村学识同科 周蒙师暮年登上第<br>第三回 周学道校士拔真才 胡屠户行凶闹捷报<br>第四回 荐亡斋和尚吃官司 打秋风乡绅遭横事<br>第五回 王秀才议立偏旁 严监生疾终正寝<br>第六回 乡绅发病闹船家 寡妇含冤控大伯&nbsp;&nbsp;<br></p>', '文学', NULL, 0, '8', 1, '0.20654329147389294'),
(7, 'bookPic/sanxia.jpg', '三侠五义', '石玉昆', '1998', '华夏出版社', '9787508013947', '<li>国家图书馆</li><li>在馆</li>', '<p>是古典长篇侠义公案小说的经典之作，堪称中国武侠小说的开山鼻祖；同时，作为中国第一部具有真正意义的武侠小说，《三侠五义》的版本众多、流传极广&nbsp;&nbsp;<br></p>', '<p>第一回 设阴谋临产换太子 奋侠义替死救皇娘<br>第二回 奎星兆梦忠良降生 雷部宣威狐狸避难<br>第三回 金龙寺英雄初救难 隐逸村狐狸三报恩<br>第四回 除妖魅包文正联姻 受皇恩定远县赴任<br>第五回 墨斗剖明皮熊犯案 乌盆诉苦别古鸣冤<br>第六回 罢官职逢义士高僧 应龙图审冤魂怨鬼&nbsp;&nbsp;<br></p>', '文学', NULL, 0, '5', 2, '0.20654329147389294'),
(8, 'bookPic/fengshen.jpg', '封神演义', '许仲琳', '1997', '人民文学出版社', '9787020024148', '<li>武汉市图书馆</li><li>在馆</li>', '<p>《封神演义》，俗称《封神榜》，又名《商周列国全传》、《武王伐纣外史》、《封神传》。全书共一百回。《封神演义》的原型最早可追溯至南宋的《武王伐纣白话文》，可能还参考了《商周演义》、《昆仑八仙东游记》，全书以武王伐纣、商周易代的历史为框架，叙写天上的神仙分成两派卷入这场斗争，支持武王的阐教，帮助纣王的为截教。&nbsp;&nbsp;<br></p>', '<p>第一回　纣王女娲宫进香<br>第二回　赏州侯苏护反商<br>第三回　姬昌解围进妲己<br>第四回　恩州驿狐狸死妲己<br>第五回　云中子进剑除妖<br>第六回　纣王无道造炮烙&nbsp;&nbsp;<br></p>', '历史', NULL, 1, '5', 2, '0.5571541164260652'),
(9, 'bookPic/shuihuhou.jpg', '水浒后传', '陈忱 ', '1983', '上海古籍出版社', '1007018', '<li>广东省图书馆</li><li>在馆</li><li>湖北省图书馆</li><li>在馆</li>', '<p>《水浒后传》是《水浒传》的续书，讲述梁山泊劫后幸存的李俊、阮小七、李应、燕青等英雄，再度聚啸山林，反对奸臣恶霸，抗击金兵入侵，最后到海岛创基立业的故事。&nbsp;&nbsp;<br></p>', '<p>第01回 阮统制感旧梁山泊 张别驾激变石碣村<br>第02回 毛孔目横吞海货 顾大嫂直斩豪家<br>第03回 病尉迟闲住受馀殃 栾廷玉失机同入伙<br>第04回 鬼脸儿寄书罹重祸 赵玉娥错配遇多情<br>第05回 老管营少妾杀命 扑天雕旧仆株连<br>第06回 饮马川群英兴旧业 虎峪寨斗法辱黄冠&nbsp;&nbsp;<br></p>', '文学', NULL, 0, '0', 0, '0'),
(10, 'bookPic/ziben.jpg', '资本论', '马克思', '2007', '北京出版社', '9787200069716', '<li>北京市图书馆</li><li>在馆</li>', '<p>经典通读:资本论》是马克思倾注毕生精力完成的科学巨著，是他一生恪守崇高信念，从事科学研究的结晶。马克思逝世后，《经典通读:资本论》第二、第三卷由恩格斯整理，分别在1885年和1894年出版。&nbsp;&nbsp;<br></p>', '<p>资本论》导读 卷一 资本的生产过程第一篇 商品和货币 第一章 商品 第二章 交换过程 第三章 货币或商品流通 第四章 货币转化为资本第二篇 绝对剩余价值的生产 第一章 劳动过程和价值增殖过程 第二章 不变资本和可变资本 第三章 剩余价值率 第四章 工作日 第五章 剩余价值率和剩余价值量第三篇 相对剩余价值的生产 第一章 相对剩余价值的概念&nbsp;&nbsp;<br></p>', '政治', NULL, 0, '0', 0, '0'),
(11, 'bookPic/daode.jpg', '道德情操论', '亚当斯密', '1997', '商务印书馆', '9775824322', '<li>国家图书馆</li><li>在馆</li><li>江苏省图书馆</li><li>在馆</li>', '<p>在《道德情操论》中，斯密谈到本性贪婪和自私的地主的行为动机时说：“富人只是从这大量的产品中选用了最贵重和最中意的东西。&nbsp;&nbsp;<br></p>', '<p>第一篇 论行为的合宜性</p><p>第二篇 论合宜感</p><p>第三篇 论同情</p>', '政治', NULL, 0, '0', 0, '0'),
(12, 'bookPic/lunli.jpg', '伦理学', '巴鲁赫·斯宾诺莎 ', '1998', '商务印书馆', '9787100011631', '<li>南京市图书馆</li><li>在馆</li>', '<p>《伦理学》一书是斯宾诺莎的主要著作，他的哲学思想基本上都表述在这本书中。他写这部著作花费了十多年的功夫，从1662年写起，到1675年才完成。&nbsp;&nbsp;<br></p>', '<p>第一部分 论神<br>第二部分 论心灵的性质和起源<br>第三部分 论情感的起源和性质<br>第四部分 论人的奴役或情感的力量<br>第五部分 论理智的力量或人的自由&nbsp;&nbsp;<br></p>', '哲学', NULL, 0, '0', 0, '0'),
(13, 'bookPic/meixue.jpg', '美学', '黑格尔', '2011', '江苏人民出版社', '9787214072481', '<li>江苏省图书馆</li><li>在馆</li><li>浙江省图书馆</li><li>在馆</li>', '<p><span style="font-size: 1.6rem;">凝练阐述现代社会严重缺失的美学的传统意义。</span><br></p><p>天生深邃的黑格尔以文化深度潜水的姿态。探索生</p><p>命、宇宙、人的行为的基本面目。他构建的哲学体系似乎可</p><p>以用一句话概括：人是由一切有生命的和无生命的物质进化</p>', '<p>总序<br>编译者语<br>导读<br>第一章美学基本概念<br>自然美是无用的，艺术家是神的代言人<br>美是理念的感性显现&nbsp;&nbsp;<br></p>', '哲学', NULL, 0, '0', 0, '0'),
(14, 'bookPic/jingjixue.jpg', '经济学原理', 'N·格里高利·曼昆 ', '2009', '北京大学出版社', '9787301150900', '<li>北京市图书馆</li><li>在馆</li>', '<p>《经济学原理(第5版):宏观经济学分册》是世界上最流行的经济学教材！其英文原版现已被哈佛大学、耶鲁大学、斯坦福大学等美国600余所大学用作经济学原理课程的教材迄今为止它已被翻译成20种语言在全世界销售100多万册！&nbsp;&nbsp;<br></p>', '<p>第8篇　宏观经济学的数据<br>第23章　一国收入的衡量<br>23.1　经济的收入与支出<br>23.2　国内生产总值的衡量<br>23.2.1　“GDP是市场价值……”<br>23.2.2　“所有的……”&nbsp;&nbsp;<br></p>', '经济', NULL, 0, '0', 0, '0'),
(15, 'bookPic/hongguan.jpg', '宏观经济学', 'N·格里高利·曼昆 ', '2009', '中国人民大学出版社', '9787300104713', '<li>福建省图书馆</li><li>在馆</li><li>北京市图书馆</li><li>在馆</li>', '<p>《宏观经济学(第6版)》作为名列第一的中级宏观经济学畅销书，曼昆的大作以可能的简洁易懂的方式涵盖了这一领域，既强调了宏观经济学的古典来源，又强调了其当前实践。&nbsp;&nbsp;<br></p>', '<p>第1篇　导言<br>　第1章　宏观经济学的科学<br>　第2章　宏观经济学的数据<br>第2篇　古典理论：长期中的经济<br>　第3章　国民收入：源自何处，去向何方<br>　第4章　货币与通货膨胀&nbsp;&nbsp;<br></p>', '经济', NULL, 0, '0', 0, '0'),
(16, 'bookPic/faxue.jpg', '法学方法论', '拉伦茨 ', '2003', '商务印书馆', '9787100037297', '<li>武汉市图书馆</li><li>在馆</li>', '<p>《法学方法论》是德国著名法学家卡尔·拉伦茨的一部重要著作，亦是法学理论方面的一部经典著作。其主要介绍了十九世纪初至一次世界大战结束期间，德国的法学理论及方法论。&nbsp;&nbsp;<br></p>', '<p>《法学方法论》导读――代译序<br>引论<br>第一章现代方法上的论辩<br>第一节由“利益法学”到“评价法学”<br>第二节关于超越法律之评价标准的问题&nbsp;&nbsp;<br></p>', '法律', NULL, 0, '0', 0, '0'),
(17, 'bookPic/xifangjpg.jpg', '西方法政哲学演讲录', '高全喜', '2004', '中国人民大学出版社', '9858123223', '<li>湖北省图书馆</li><li>在馆</li>', '<p>这是本演讲录，话题主要围绕西方法政哲学来展开。全书共分11部分，重点在于后面10个部分，一个部分一个主讲人。主讲人都是活跃在国内思想界的大家，如高全喜、徐友渔、曹卫东等。&nbsp;&nbsp;<br></p>', '<p>一、霍布斯的生平与著作</p><p>二、霍布斯的研究状况简介</p><p>三、霍布斯政治哲学概述</p>', '法律', NULL, 0, '0', 0, '0'),
(18, 'bookPic/huizu.jpg', '回族婚姻家庭面面观', '文化 ', '2007', '民族出版社', '9787311029500', '<li>甘肃省图书馆</li><li>在馆</li>', '<p>无</p>', '<p>无</p>', '文化', NULL, 0, '0', 0, '0'),
(19, 'bookPic/TED.jpg', '像TED一样演讲', '卡迈恩·加洛 ', '2015', '中信出版社', '9787508649412', '<li>国家图书馆</li><li>在馆</li><li>广东省图书馆</li><li>在馆</li>', '<p>《像TED一样演讲》认为，思想是21世纪的货币，因此，想要取得成功，你必须具备推销你的思想以及你自己的沟通力，它是能够帮你取得成功的最伟大的技能。&nbsp;&nbsp;<br></p>', '<p>序 言 // I<br>第一部分 情 感<br>第1章 释放你内心的热情 // 003<br>是什么让你的心灵歌唱？ // 010<br>世界上最幸福的人 // 011<br>你为什么无法成就伟业？ // 013&nbsp;&nbsp;<br></p>', '文化', NULL, 0, '0', 0, '0'),
(20, 'bookPic/tanmei.jpg', '谈美书简', '朱光潜', '2004', '北京出版社', '9787200050455', '<li>北京市图书馆</li><li>在馆</li>', '<p>本书是朱光潜先生在82岁高龄的情况下写就的晚年心血之作，全书由十三封书信结集而成。深入探讨了美学、艺术、文学中的美和美感、审美范畴、形象思维、文学创作及人性论等一系列问题&nbsp;&nbsp;<br></p>', '<p>一代前言：怎样学美学？<br>二从现实生活出发还是从抽象概念出发？<br>三谈人<br>四关于马克思主义与美学的一些误解<br>五艺术是一种生产劳动<br>六冲破文艺创作和美学中的一些禁区&nbsp;&nbsp;<br></p>', '艺术', NULL, 0, '0', 0, '0'),
(21, 'bookPic/tangshi.jpg', '唐宋词欣赏', '夏承焘 ', '2000', '北京出版社', '9787200044713', '<li>南京市图书馆</li><li>在馆</li>', '<p>《唐宋词欣赏》是四十多年前先生为广大读者欣赏唐宋词的需要而说解的，大半由先生的助手怀霜记录整理，经先生改定，发表于杭州、上海和香港的报刊上。&nbsp;&nbsp;<br></p>', '<p>词的形式<br>长短句<br>盛唐时代民间流行的曲子词<br>敦煌曲子词<br>中唐时代的文人词<br>花间词体&nbsp;&nbsp;<br></p>', '艺术', NULL, 0, '0', 0, '0'),
(22, 'bookPic/diqiu.jpg', '地球之美', '帕特里克·德韦弗 文', '2017', '新星出版社', '9787513324052', '<li>江苏省图书馆</li><li>在馆</li>', '<p>★一部看得见的地球简史！</p><p>★200个改写地球命运的时刻，呈现46亿年漫长的地球史！</p><p>★法国国家自然历史博物馆、法国地质学会、法国国家地质遗产、联合国教科文组织 专家权威撰写</p>', '<p>无</p>', '地理', NULL, 0, '0', 0, '0'),
(23, 'bookPic/haiyang.jpg', '海洋与文明', '林肯·佩恩 ', '2017', '天津人民出版社', '9787201114576', '<li>天津市图书馆</li><li>在馆</li>', '<p>一部海洋视角的世界史，一部关于航海者的史诗</p><p>本书荣获美国《选择》杂志2014年度“杰出学术著作奖”</p>', '<p>引 言<br>第 1 章 通向海洋<br>第 2 章 古埃及的河流与海洋<br>第 3 章 青铜时代的航海活动<br>第 4 章 腓尼基人、希腊人与地中海<br>第 5 章 迦太基、罗马与地中海&nbsp;&nbsp;<br></p>', '自然科学', NULL, 0, '0', 0, '0'),
(24, 'bookPic/wangluo.jpg', '计算机网络', 'Andrew S·Tanenbaum ', '2004', '清华大学出版社', '9787302089773', '<li>北京市图书馆</li><li>在馆</li>', '<p>计算机网络（第4版）》全书按照网络协议模型（物理层、数据链路层、介质访问控制子层、网络层、传输层和应用层），自下而上系统地介绍了计算机网络的基本原理，并给出了大量实例。在讲述各网络层的同时，还融合进了近年来迅速发展起来的各种网络技术，&nbsp;&nbsp;<br></p>', '<p>第1章概述<br>1.1计算机网络的应用<br>1.1.1商业应用<br>1.1.2家庭应用<br>1.1.3移动用户<br>1.1.4社会问题&nbsp;&nbsp;<br></p>', '自然科学', NULL, 0, '0', 0, '0'),
(25, 'bookPic/suanfa.jpg', '算法导论（原书第2版）', 'Thomas H.Cormen', '2006', '机械工业出版社', '9787111187776', '<li>南京市图书馆</li><li>在馆</li><li>武汉市图书馆</li><li>在馆</li>', '<p>这本书深入浅出，全面地介绍了计算机算法。对每一个算法的分析既易于理解又十分有趣，并保持了数学严谨性。本书的设计目标全面，适用于多种用途。涵盖的内容有：算法在计算中的作用，概率分析和随机算法的介绍。书中专门讨论了线性规划，介绍了动态规划的两个应用，&nbsp;&nbsp;<br></p>', '<p>出版者的话<br>专家指导委员会<br>译者序<br>前言<br>第一部分 基础知识<br>引言&nbsp;&nbsp;<br></p>', '工业技术', NULL, 0, '0', 0, '0'),
(26, 'bookPic/biancheng.jpg', '编程珠玑', 'Jon Bentley ', '2008', '人民邮电出版社', '9787115179289', '<li>北京市图书馆</li><li>在馆</li>', '<p>本书是计算机科学方面的经典名著。书的内容围绕程序设计人员面对的一系列实际问题展开。作者Jon Bentley 以其独有的洞察力和创造力，引导读者理解这些问题并学会解决方法，而这些正是程序员实际编程生涯中至关重要的。&nbsp;&nbsp;<br></p>', '<p>第一部分　基础<br>第1章　开篇<br>1.1　一次友好的对话<br>1.2　准确的问题描述<br>1.3　程序设计<br>1.4　实现概要&nbsp;&nbsp;<br></p>', '工业技术', NULL, 0, '0', 0, '0'),
(27, 'bookPic/shenru.jpg', '深入理解计算机系统（原书第2版）', 'David O''Hallaron ', '2011', '机械工业出版社', '9787111321330', '<li>武汉市图书馆</li><li>在馆</li>', '<p>本书从程序员的视角详细阐述计算机系统的本质概念，并展示这些概念如何实实在在地影响应用程序的正确性、性能和实用性。全书共12章，主要内容包括信息的表示和处理、程序的机器级表示、处理器体系结构、优化程序性能、存储器层次结构、链接、异常控制流、虚拟存储器&nbsp;&nbsp;<br></p>', '<p>出版者的话<br>译者序<br>前　言<br>第1章　计算机系统漫游1<br>1.1　信息就是位+上下文1<br>1.2　程序被其他程序翻译成不同的格式3&nbsp;&nbsp;<br></p>', '工业技术', NULL, 0, '0', 0, '0'),
(28, 'bookPic/bianyi.jpg', '编译原理', 'Jeffrey D. Ullman ', '2008', '机械工业出版社', '9787111251217', '<li>北京市图书馆</li><li>在馆</li>', '<p>本书全面、深入地探讨了编译器设计方面的重要主题，包括词法分析、语法分析、语法制导定义和语法制导翻译、运行时刻环境、目标代码生成、代码优化技术、并行性检测以及过程间分析技术，并在相关章节中给出大量的实例。&nbsp;&nbsp;<br></p>', '<p>出版者的话<br>译者序<br>前言<br>第1章 引论<br>1.1 语言处理器<br>1.2 一个编译器的结构&nbsp;&nbsp;<br></p>', '工业技术', NULL, 0, '0', 0, '0'),
(29, 'bookPic/shengwu.jpg', '生命的壮阔', '斯蒂芬·杰·古尔德 ', '2001', '生活·读书·新知三联书店', '9787108014900', '<li>武汉市图书馆</li><li>在馆</li>', '<p>古尔德告诉读者，生物从简单到复杂、从低级到高级的变化并不是自然界普遍的铁律，把“进化”说成趋势是人类中心主义演绎出来的神话。他用了极其简单易懂的实例，雄辩地分析了流行于科学界并广为普通人接受的观点中包藏的迷误。&nbsp;&nbsp;<br></p>', '<p>第一部趋势何处寻<br>······第1章赫胥黎的棋盘<br>······第2章被宣传扭曲的达尔文<br>······第3章历史趋势的铸造<br>第二部死亡与马<br>······第4章第一例：我的致命癌症&nbsp;&nbsp;<br></p>', '生物医药', NULL, 0, '0', 0, '0'),
(30, 'bookPic/daerwen.jpg', '自达尔文以来', '古尔德(美) ', '1997', '三联书店', '9787108010919', '<li>湖北省图书馆</li><li>在馆</li>', '<p>无</p>', '<p>目录<br>序言<br>第一部分 关于达尔文<br>1.达尔文的拖延<br>2.达尔文在船上位置的变换，或五年伴在船长的餐桌旁<br>3.达尔文的难题：进化的艰苦历程&nbsp;&nbsp;<br></p>', '生物医药', NULL, 0, '0', 0, '0'),
(31, 'bookPic/huluobo.jpg', '一根萝卜的革命', '藤田和芳 ', '2013', '生活·读书·新知三联书店', '9787108045140', '<li>国家图书馆</li><li>在馆</li><li>浙江省图书馆</li><li>在馆</li>', '<p>藤田和芳，日本最早推行有机农业的先驱者之一，于1975年8月创立日本守护大地协会，现在的守护大地协会已发展成为拥有2500个生产会员、9.1万个消费会员、年营业额达150多亿日元(超过10亿元人民币)的庞大组织。曾被美国《新闻周刊》评为“改变世界的社会企业家100人”。&nbsp;&nbsp;<br></p>', '<p>无</p>', '农业科学', NULL, 0, '0', 0, '0'),
(32, 'bookPic/nongmin.jpg', '我在美国当农民', '石嫣 ', '2012', '生活·读书·新知三联书店', '9787108040206', '<li>武汉市图书馆</li><li>在馆</li>', '<p>大学毕业时，她对未来的职业规划非常模糊；硕士读了两年，还是找不到为之奋斗一生的事业；只好继续读博士；再后来，她去了美国。本该搞点洋玩意，却在“荒郊野岭”的非典型资本主义乡村种了半年地。就是这段“另类洋插队”的经历，彻底改变了她的人生。&nbsp;&nbsp;<br></p>', '<p>无</p>', '农业科学', NULL, 0, '0', 0, '0'),
(33, 'bookPic/guidao.jpg', '轨道上的世界', '张道海 ', '2013', '人民交通出版社', '9787114106538', '<li>广西省图书馆</li><li>在馆</li>', '<p>刘龙胜等著的《轨道上的世界--东京都市圈城市和交通研究》以东京都市圈轨道交通为主线，紧扣都市圈城市与交通互动发展这一主题，系统且深入的研究了东京都市圈城市和交通发展，包括都市圈社会经济情况、土地利用特征&nbsp;&nbsp;<br></p>', '<p>第一章 都市圈基本情况<br>第一节 圈层区划<br>第二节 在日本的中心地位<br>第三节 职能分工与中心体系<br>第四节 社会经济<br>第五节 东京都土地利用&nbsp;&nbsp;<br></p>', '交通运输', NULL, 0, '0', 0, '0'),
(34, 'bookPic/zhancheng.jpg', '站城一体开发', '日建设计站城一体开发研究会 ', '2014', '中国建筑工业出版社', '9787112162864', '<li>北京市图书馆</li><li>在馆</li>', '<p>车站及其周边地区是日本城市更新改造最活跃的地区，本书通过站城一体化开发的案例，对车站与城市的关系，车站的选址、开发规模的设定、土地复合利用模式等规划课题进行了探讨，&nbsp;&nbsp;<br></p>', '<p>无</p>', '交通运输', NULL, 0, '0', 0, '0'),
(35, 'bookPic/renju.jpg', '人居环境科学导论', '吴良镛 ', '2001', '中国建工', '9787112045068', '<li>武汉市图书馆</li><li>在馆</li>', '<p>《人居环境科学导论》内容包括两部分：第一部分“人居环境科学释义”阐述了人居环境科学的来由、人居环境的构成、人居环境建设的基本观念、人居环境科学的方法论，以及在保护和建设可持续发展的人居环境方面的研究实例；&nbsp;&nbsp;<br></p>', '<p>无</p>', '环境科学', NULL, 0, '0', 0, '0'),
(36, 'bookPic/zaoyuan.jpg', '中国造园论', '张家骥 ', '2012', '山西人民出版社', '9787203041795', '<li>武汉市图书馆</li><li>在馆</li>', '<p>中国造园论》由张家骥所著，是我国第一部具有现代科学意义的造园学系统理论著作。本书通过十个专题的论述，构成一个完整而系统的中国造园的思想体系。本书开宗明义，从文化与传统的概念及其与中国造园的关系&nbsp;&nbsp;<br></p>', '<p>无</p>', '环境科学', NULL, 0, '0', 0, '0');

