INSERT INTO `collect` (`mid`, `mPath`, `mName`, `mAuthor`, `mTime`, `mPub`, `mIsbn`, `mLib`, `mIntro`, `mCata`, `mInte`, `User_ID`) VALUES
('4028b8816304f8c5016304fd44430010', 'bookPic/fengshen.jpg', '封神演义', '许仲琳', '1997', '人民文学出版社', '9787020024148', '<li>武汉市图书馆</li><li>在馆</li>', '<p>《封神演义》，俗称《封神榜》，又名《商周列国全传》、《武王伐纣外史》、《封神传》。全书共一百回。《封神演义》的原型最早可追溯至南宋的《武王伐纣白话文》，可能还参考了《商周演义》、《昆仑八仙东游记》，全书以武王伐纣、商周易代的历史为框架，叙写天上的神仙分成两派卷入这场斗争，支持武王的阐教，帮助纣王的为截教。&nbsp;&nbsp;<br></p>', '<p>第一回　纣王女娲宫进香<br>第二回　赏州侯苏护反商<br>第三回　姬昌解围进妲己<br>第四回　恩州驿狐狸死妲己<br>第五回　云中子进剑除妖<br>第六回　纣王无道造炮烙&nbsp;&nbsp;<br></p>', '历史', '4028b8815e37d2d1015e37d31fb50000'),
('4028b8816304f8c5016304fd61d60011', 'bookPic/honglou.jpg', '红楼梦', '曹雪芹', '1996', '人民文学出版社', '9787020002207', '<li>武汉市图书馆</li><li>在馆</li><li>广东省图书馆</li><li>在馆</li>', '<p>《红楼梦》是一部百科全书式的长篇小说。以宝黛爱情悲剧为主线，以四大家族的荣辱兴衰为背景，描绘出18世纪中国封建社会的方方面面，以及封建专制下新兴资本主义民主思想的萌动。结构宏大、情节委婉、细节精致，人物形象栩栩如生，声口毕现，堪称中国古代小说中的经 典。&nbsp;&nbsp;<br></p>', '<p>第一回 甄士隐梦幻识通灵 贾雨村风尘怀闺秀<br>第二回 贾夫人仙逝扬州城 冷子兴演说荣国府<br>第三回 托内兄如海酬训教 接外孙贾母惜孤女<br>第四回 薄命女偏逢薄命郎 葫芦僧乱判葫芦案<br>第五回 游幻境指迷十二钗 饮仙醪曲演红楼梦<br>第六回 贾宝玉初试云雨情 刘姥姥一进荣国府&nbsp;&nbsp;<br></p>', '文学', '4028b8815e37d2d1015e37d31fb50000'),
('4028b8816304f8c5016304fd89e20012', 'bookPic/xiyou.jpg', '西游记', '吴承恩', '1994', '岳麓书社', '9787805200552', '<li>辽宁省图书馆</li><li>在馆</li>', '<p>《西游记》的艺术虚构正是建立在传统艺术经验和这种社会的宗教性观念和风习的基础之上的，但它又以作者融会了传统艺术经验所形成的艺术的独创性批判了社会的宗教性观念，或更正确地说，和社会的宗教性观念开了玩笑，进行了潮弄。这是这部演述超人间故事的神魔小说最突出、最优异的品质，也是它的艺术价值和魅力的最根本的所在。&nbsp;&nbsp;<br></p>', '<p>第一回 灵根育孕源流出 心性修持大道生<br>第二回 悟彻菩提真妙理 断魔归本合元神<br>第三回 四海千山皆拱伏 九幽十类尽除名<br>第四回 官封弼马心何足 名注齐天意未宁&nbsp;&nbsp;<br></p>', '文学', '4028b8815e37d2d1015e37d31fb50000'),
('4028b8816304f8c5016304fddcfa0013', 'bookPic/liaozhai.jpg', '聊斋志异', '蒲松龄', '2002', '岳麓书社', '9787806651452', '<li>甘肃省图书馆</li><li>在馆</li><li>国家图书馆</li><li>在馆</li>', '<p>《聊斋志异》是我国清初的一部文言短篇小说集，共收小说近500篇，或讲民间的民俗民习、奇谈异闻、或讲世间万物的奇异变幻、题材极为广泛。其独特的故事情节、异彩纷呈的艺术形象，作者蒲松龄一生科甲不利、生活清贫，对于当时的社会有着深刻的认识。&nbsp;&nbsp;<br></p>', '<p>1 考城隍<br>2 耳中人<br>卷二<br>1 金世成<br>2 董生&nbsp;&nbsp;<br></p>', '文学', '4028b8815e37d2d1015e37d31fb50000'),
('4028b881630502dc01630507ed1e0000', 'bookPic/shuihu.jpg', '水浒传', '施耐庵', '2005', '人民文学出版社', '9787020008742', '<li>国家图书馆</li><li>在馆</li><li>苏州市图书馆</li><li>在馆</li><li>沈阳市图书馆</li><li>借出</li>', '<p>《水浒传》是我国第一部以农民起义为题材的长篇章回小说，是我国文学史上一座巍然屹立的丰碑，也是世界文学宝库中一颗光彩夺目的明珠。数百年来，它一直深受我国人民的喜爱，并被译为多种文字，成为我国流传最为广泛的古代长篇小说之一。&nbsp;&nbsp;<br></p>', '<p>第一回 张天师祈禳瘟疫 洪太尉误走妖魔<br>第二回 王教头私走延安府 九纹龙大闹史家村<br>第三回 史大郎夜走华阴县 鲁提辖拳打镇关西<br>第四回 赵员外重修文殊院 鲁智深大闹五台山<br>第五回 小霸王醉入销金帐 花和尚大闹桃花村&nbsp;&nbsp;<br></p>', '文学', '4028b8815e9e683e015e9e692a8b0000'),
('4028b881630502dc0163050819130001', 'bookPic/liaozhai.jpg', '聊斋志异', '蒲松龄', '2002', '岳麓书社', '9787806651452', '<li>甘肃省图书馆</li><li>在馆</li><li>国家图书馆</li><li>在馆</li>', '<p>《聊斋志异》是我国清初的一部文言短篇小说集，共收小说近500篇，或讲民间的民俗民习、奇谈异闻、或讲世间万物的奇异变幻、题材极为广泛。其独特的故事情节、异彩纷呈的艺术形象，作者蒲松龄一生科甲不利、生活清贫，对于当时的社会有着深刻的认识。&nbsp;&nbsp;<br></p>', '<p>1 考城隍<br>2 耳中人<br>卷二<br>1 金世成<br>2 董生&nbsp;&nbsp;<br></p>', '文学', '4028b8815e9e683e015e9e692a8b0000'),
('4028b881630502dc0163050e81550002', 'bookPic/sanguo.jpg', '三国演义', '罗贯中', '2010', '岳麓书社', '9787805200132', '<li>武汉市图书馆</li><li>在馆</li><li>国家图书馆</li><li>在馆</li><li>江苏省图书馆</li><li>在馆</li>', '<p>《三国演义》是根据三国时期的史实和民间传说创作而成的优秀历史小说。它的内容丰富多彩，为读者留下了深厚多而的认识价值。故事远起汉灵帝年间刘、关、张桃园结义，描述了东汉末年和三国时期近百年发生的重大历史事件，和众多的叱咤风云的英雄人物。作者通过真实动人的故事，揭示了封建统治阶级内部的黑暗和腐朽，控诉了统治者的暴虐和丑恶。&nbsp;&nbsp;<br></p>', '<p>第一回 宴桃园豪杰三结义 斩黄巾英雄首立功</p><p>第二回 张翼德怒鞭督邮 何国舅谋诛宦竖</p><p>第三回 议温明董卓叱丁原 馈金珠李肃说吕布&nbsp;</p><p>第四回 废汉帝陈留践位 谋董贼孟德献刀&nbsp;</p><p>第五回 发矫诏诸镇应曹公 破关兵三英战吕布</p>', '历史', '4028b8815e9e683e015e9e692a8b0000');

