--
-- 视图结构 `book_score`
--
DROP TABLE IF EXISTS `book_score`;

CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `book_score` AS select `book`.`bid` AS `bid`,`book`.`Path` AS `Path`,`book`.`Name` AS `Name`,`book`.`Author` AS `Author`,`book`.`Time` AS `Time`,`book`.`Publish` AS `Publish`,`book`.`Isbn` AS `Isbn`,`book`.`Library` AS `Library`,`book`.`Introdu` AS `Introdu`,`book`.`boInte` AS `boInte`,`book`.`bofrie` AS `bofrie`,`book`.`collect` AS `collect`,`book`.`boScore` AS `boScore`,`book`.`boPeople` AS `boPeople`,`score`.`sid` AS `sid`,`score`.`scoUser` AS `scoUser`,`score`.`Book_ID` AS `Book_ID` from (`book` join `score` on((`score`.`Book_ID` = `book`.`bid`)));

-- --------------------------------------------------------

